object_destructuring.code:
const utilisateur = { nom: 'Alice', age: 30 };
const { nom, age } = utilisateur;
console.log(nom); // 'Alice'
console.log(age); // 30

array_destructuring.code:
const nombres = [1, 2, 3, 4, 5];
const [premier, deuxieme, ...reste] = nombres;
console.log(premier); // 1
console.log(deuxieme); // 2
console.log(reste); // [3, 4, 5]

default_values.code:
const { nom = 'Inconnu', age = 0 } = utilisateur;
console.log(nom); // 'Alice'
console.log(age); // 30

aliasing.code:
const { nom: prenom, age: annee } = utilisateur;
console.log(prenom); // 'Alice'
console.log(annee); // 30

nested_destructuring.code:
const utilisateur = { nom: 'Alice', adresse: { ville: 'Paris', codePostal: '75001' } };
const { nom, adresse: { ville, codePostal } } = utilisateur;
console.log(ville); // 'Paris'
console.log(codePostal); // '75001'
