Exercice1.correction:
function sumArray(numbers) {
  return numbers.reduce((sum, number) => sum + number, 0);
}

Exercice2.correction:
function countVowels(string) {
  const vowels = 'aeiouAEIOU';
  return string.split('').filter(char => vowels.includes(char)).length;
}

Exercice4.correction:
function findOldestPerson(people) {
  return people.reduce((oldest, person) => (oldest.age > person.age) ? oldest : person).name;
}
