Exercice4.correction:
function isEvenOrOdd(number) {
  if (number % 2 === 0) {
    return 'even';
  } else {
    return 'odd';
  }
}
console.log(isEvenOrOdd(7));

Exercice5.correction:
function findLargestElement(array) {
  let largest = array[0];
  for (let i = 1; i < array.length; i++) {
    if (array[i] > largest) {
      largest = array[i];
    }
  }
  return largest;
}
const numbers = [5, 12, 3, 8, 21];
console.log(findLargestElement(numbers));

Exercice6.correction:
function factorial(n) {
  if (n === 0) {
    return 1;
  } else {
    return n * factorial(n - 1);
  }
}
console.log(factorial(5));

Exercice7.correction:
function isPalindrome(string) {
  const reversedString = string.split('').reverse().join('');
  return string === reversedString;
}
console.log(isPalindrome('radar'));

Exercice8.correction:
function isPrimeNumber(number) {
  if (number <= 1) {
    return false;
  }
  for (let i = 2; i <= Math.sqrt(number); i++) {
    if (number % i === 0) {
      return false;
    }
  }
  return true;
}
function generateFirstNPrimeNumbers(n) {
  const primeNumbers = [];
  let number = 2;
  while (primeNumbers.length < n) {
    if (isPrimeNumber(number)) {
      primeNumbers.push(number);
    }
    number++;
  }
  return primeNumbers;
}
console.log(generateFirstNPrimeNumbers(10));

