// Exemple de fonction récursive pour rechercher un élément dans un arbre binaire
function rechercheArbre(arbre, valeur) {
    if (!arbre) {
        return false;
    }
    if (arbre.valeur === valeur) {
        return true;
    }
    return rechercheArbre(arbre.gauche, valeur) || rechercheArbre(arbre.droite, valeur);
}

