acces_modification.code:
let fruits = ['pomme', 'banane', 'orange'];

// Accéder à un élément
let premierFruit = fruits[0];

// Modifier un élément
fruits[1] = 'kiwi';

ajout_suppression.code:
let fruits = ['pomme', 'banane', 'orange'];

// Ajouter un élément à la fin
fruits.push('kiwi');

// Supprimer le dernier élément
fruits.pop();

// Ajouter un élément au début
fruits.unshift('fraise');

// Supprimer le premier élément
fruits.shift();

recherche.code:
let fruits = ['pomme', 'banane', 'orange'];

// Rechercher l'indice d'un élément
let indice = fruits.indexOf('banane');

// Vérifier si un élément existe
let existe = fruits.includes('kiwi');

// Trouver le premier élément qui satisfait une condition
let fruitCher = fruits.find(fruit => fruit === 'pomme');

// Filtrer les éléments qui satisfont une condition
let fruitsLongs = fruits.filter(fruit => fruit.length > 5);

iteration.code:
let fruits = ['pomme', 'banane', 'orange'];

// Utilisation d'une boucle for
for (let i = 0; i < fruits.length; i++) {
    console.log(fruits[i]);
}

// Utilisation de forEach()
fruits.forEach(fruit => {
    console.log(fruit);
});

// Utilisation de map() pour créer un nouveau tableau
let longueurs = fruits.map(fruit => fruit.length);

// Utilisation de reduce() pour réduire le tableau à une seule valeur
let totalLongueur = fruits.reduce((total, fruit) => total + fruit.length, 0);
