Exercice1.correction:
interface iForme {
  public function calculerAire();
}

class Cercle implements iForme {
  public $rayon;

  public function __construct($rayon) {
    $this->rayon = $rayon;
  }

  public function calculerAire() {
    return pi() * pow($this->rayon, 2);
  }
}

