class Rectangle {
  public $longueur;
  public $largeur;

  public function aire() {
    return $this->longueur * $this->largeur;
  }
}

$rect = new Rectangle();
$rect->longueur = 5;
$rect->largeur = 3;
echo 'Aire du rectangle : ' . $rect->aire();

