class Cercle {
  public $rayon;

  public function circonference() {
    return 2 * 3.14159 * $this->rayon;
  }
}

$cerc = new Cercle();
$cerc->rayon = 7;
echo 'Circonférence du cercle : ' . $cerc->circonference();
